package org.example;import org.apache.pdfbox.pdmodel.PDDocument;import org.apache.pdfbox.pdmodel.font.PDType1Font;import java.io.File;import java.io.IOException;import java.io.InputStream;public class Main {    public static void main(String[] args) {        ClassLoader cl = Main.class.getClassLoader();        InputStream inputText = cl.getResourceAsStream("text.txt");        InputStream inputImg = cl.getResourceAsStream("obrazek.png");        PDDocument doc = new PdfBuilder()                .addTextFormat(2.0f, 12, PDType1Font.HELVETICA)                .addPaths(inputText, inputImg)                .build();        try {            doc.save(new File("target/output.pdf"));        } catch (IOException e) {            throw new RuntimeException(e);        }    }}